import React from 'react';
import Table from './Table';
import xBackground from './images/x.gif';
import oBackground from './images/o.gif';

class Matrix extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      xSelection: true,
      oSelection: false,
      imgs: {
        xImg: `url(${xBackground})`,
        oImg: `url(${oBackground})`,
      }
    };
  }

  showSelection = () => {
    return (
      <section>
        <h4>Make your choice:</h4>
        <div><img alt="O selection" src={this.state.imgs.oImg}></img></div>
        <div><img alt="X selection" src={this.state.imgs.xImg}></img></div>
      </section>
    );
  }

  render() {
    return (
      <Table />
    );
  }
}

export default Matrix;
