import React from 'react';
import '../App.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      index: 0,

      hashImages: ['X', 'O'],
    }
    this.insertImage.bind(this);
  }

  insertImage() {
    this.setState({
      index: this.state.index ? 0 : 1,
    });
    return this.state.hashImages[this.state.index]
  }

  render() {
    return (
      <table>
        <tbody>
