import React from 'react';
import Table from './Table';
import '../App.css';

const xOption = require('./images/x.gif');
const oOption = require('./images/o.gif');

class ShowSelection extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      index: 0,
      hashImages: [xOption, oOption],
    }
  }

  render() {
    console.log(xOption, oOption);
    return (
      <section>
        <h4>Make your choice:</h4>
        <div>
          <div><img className="Hash_image-selection" alt="O selection" src={this.state.hashImages[1]}></img></div>
          <div><img className="Hash_image-selection" alt="X selection" src={this.state.hashImages[0]}></img></div>

        </div>
      </section >
    );
  }
}

class Matrix extends React.Component {
  render() {
    return (
      <div>
        <Table />
        <ShowSelection />
      </div>
    );
  }
}

export default Matrix;
