import React from 'react';

const resposta = {
  data: ['This', 'is', 'a', 'map']
}

class EasyMap extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lista: [],
    };
    this.mapGenerate = this.mapGenerate.bind(this);
  }

  mapGenerate() {
    this.setState({
      lista: resposta.data,
    });
    console.log(resposta.data);
  }

  render() {
    return (
      <div>
        <button onClick={this.mapGenerate}>Click Me</button>
      </div>
    );
  }
}

export default EasyMap;
