import React from 'react';
import Table from './Table';
import xImage from './images/x.gif';
import oImage from './images/o.gif';
import '../App.css';

const xOption = require('./images/x.gif');
const oOption = require('./images/o.gif');

class ShowSelection extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      index: 0,
      hashImages: [xOption, oOption],
    }
  }

  render() {
    return (
        <div className="Matrix_father-div">
        <h4>Make your choice:</h4>
        <div className="Matrix_images-selection">
          <img className="Hash_image-selection" alt="O selection" src={oImage}></img>
          <img className="Hash_image-selection" alt="X selection" src={xImage}></img>
        </div>
      </div >
    );
  }
}

class Matrix extends React.Component {
  render() {
    return (
      <div>
        <Table />
        <ShowSelection />
      </div>
    );
  }
}

export default Matrix;
