import React from 'react';
import '../App.css';

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      index: 0,
      hashImages: ['X', 'O'],
      tab11: false,
      tab12: false,
      tab13: false,
      tab21: false,
      tab22: false,
      tab23: false,
      tab31: false,
      tab32: false,
      tab33: false,
    }
  }

  insertValueCell(index, tabValue) {
    this.setState({
      index : index ? 0 : 1,
      tabValue: true,
    });
  }

  render() {
    return (
      <table>
        <tbody>
          <tr>
            <td className="Table_cell" onClick={() => this.insertValueCell(this.state.index, this.state.tab11)}>
              <p className="Table_value_x-Or-o"></p>
            </td>
            <td className="Table_cell" onClick={() => this.insertValueCell()}>
            </td>
            <td className="Table_cell" onClick={() => this.insertValueCell()}>
            </td>
          </tr>
          <tr>
            <td className="Table_cell" onClick={() => this.insertValueCell()}>
