import React from 'react';
import Table from './Table';

class ShowSelection extends React.component {
  constructor(props) {
    super(props);

    const xOption = require('./images/x.gif');
    const oOption = require('./images/o.gif');

    this.state = {
      index: 0,
      hashImages: [xOption, oOption],
    }
  }
  render() {
    return (
      <section>
        <h4>Make your choice:</h4>
        <div><img alt="O selection" src={this.state.imgs.oImg}></img></div>
        <div><img alt="X selection" src={this.state.imgs.xImg}></img></div>
      </section >
    );
  }
}

class Matrix extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      xSelection: true,
      oSelection: false,
      imgs: {
        xImg: `url(${xBackground})`,
        oImg: `url(${oBackground})`,
      }
    };
  }


  render() {
    return (
      <Table />
    );
  }
}

export default Matrix;
